                >
                  <Icon size={24} className={benefit.iconColor} strokeWidth={2} />
                </div>

                {/* Title */}
                <h3 className="text-lg font-semibold text-text mb-2">
                  {t(`benefits.${benefit.key}.title`)}
                </h3>

                {/* Description */}
