 * MonitorizeazƒÉ plƒÉ»õile de succes pentru statistici
 */
async function handlePaymentSuccess(paymentIntent: Stripe.PaymentIntent): Promise<void> {
  console.log(`‚úÖ Payment Intent reu»ôit: ${paymentIntent.id}`);
  console.log(`   Suma: ${paymentIntent.amount / 100} ${paymentIntent.currency.toUpperCase()}`);

  // √én viitor, aici putem adƒÉuga tracking pentru succese
  // pentru a calcula rate de succes vs e»ôec
}

