  let errorCount = 0;

  for (const recipe of recipeImages) {
    try {
      console.log(`üîΩ Downloading: ${recipe.title}...`);

      // Download image to temp directory
      const localPath = path.join(tempDir, recipe.filename);
      await downloadImage(recipe.url, localPath);

      console.log(`   ‚úÖ Downloaded to: ${localPath}`);

      // Read file for upload
      const fileBuffer = fs.readFileSync(localPath);
      const file = new File([fileBuffer], recipe.filename, { type: 'image/jpeg' });

      console.log(`   ‚¨ÜÔ∏è  Uploading to Supabase Storage...`);

      // Upload to Supabase Storage
      const storagePath = `${recipe.id}/${recipe.filename}`;
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('recipe-images')
        .upload(storagePath, fileBuffer, {
          contentType: 'image/jpeg',
          upsert: true,
        });

      if (uploadError) {
        throw new Error(`Upload failed: ${uploadError.message}`);
      }

      // Get public URL
      const { data: urlData } = supabase.storage.from('recipe-images').getPublicUrl(storagePath);

      const imageUrl = urlData.publicUrl;

      console.log(`   üîó Public URL: ${imageUrl}`);

      // Update recipe in database
      console.log(`   üíæ Updating database record...`);

      const { error: updateError } = await supabase
        .from('recipes')
        .update({
          image_url: imageUrl,
          updated_at: new Date().toISOString(),
        })
        .eq('id', recipe.id);

      if (updateError) {
        throw new Error(`Database update failed: ${updateError.message}`);
      }

      console.log(`   ‚úÖ Successfully processed: ${recipe.title}\n`);
      successCount++;

      // Clean up temp file
      fs.unlinkSync(localPath);
    } catch (error) {
      console.error(`   ‚ùå Error processing ${recipe.title}: ${error.message}\n`);
      errorCount++;
    }
  }

  // Clean up temp directory
  try {
    fs.rmSync(tempDir, { recursive: true });
  } catch (error) {
    console.warn('‚ö†Ô∏è  Could not clean up temp directory:', error.message);
  }

  console.log('üéâ Recipe image processing completed!');
  console.log(`‚úÖ Successful: ${successCount}`);
  console.log(`‚ùå Failed: ${errorCount}`);

  if (successCount > 0) {
    console.log('\nüìã Next steps:');
    console.log('   1. Test image loading in the web application');
    console.log('   2. Verify responsive sizing on different devices');
    console.log('   3. Check image optimization through Vercel');
    console.log('   4. Test fallback behavior for any remaining null images');
  }
}

function downloadImage(url, filepath) {
  return new Promise((resolve, reject) => {
    const file = fs.createWriteStream(filepath);

    https
      .get(url, (response) => {
        if (response.statusCode !== 200) {
          reject(new Error(`Failed to download image: HTTP ${response.statusCode}`));
          return;
        }

        response.pipe(file);

        file.on('finish', () => {
          file.close();
          resolve();
        });

        file.on('error', (error) => {
          fs.unlink(filepath, () => {}); // Delete the file on error
          reject(error);
        });
      })
      .on('error', (error) => {
        reject(error);
      });
  });
}

// Mock File class for Node.js environment
if (typeof File === 'undefined') {
  global.File = class File {
    constructor(buffer, name, options = {}) {
      this.buffer = buffer;
      this.name = name;
      this.type = options.type || 'application/octet-stream';
      this.size = buffer.length;
      this.lastModified = Date.now();
    }
  };
}

