if (require.main === module) {
  downloadRecipeImages()
    .then(() => {
      console.log('üì∏ Image download and upload process completed!');
      process.exit(0);
    })
    .catch((error) => {
      console.error('üí• Process failed:', error);
      process.exit(1);
    });
}

